// section2 background image load script
  window.onload = function() {
    // Select the element with the background image
    var iframeVideoElement = document.querySelector('.section2 .sliderscroller a.iframevideo.odd-image');

    // Get the computed background image URL
    if (iframeVideoElement) {
      var backgroundImage = window.getComputedStyle(iframeVideoElement).backgroundImage;

      // Apply the background image to the .section2 element
      var section2Element = document.querySelector('.section2');
      if (backgroundImage && section2Element) {
        section2Element.style.backgroundImage = backgroundImage;
      }
    }
  }




